const add = (firstNumber, secondNumber) => Number(firstNumber) + Number(secondNumber);
const minus = (firstNumber, secondNumber) => Number(firstNumber) - Number(secondNumber);
const divide = (firstNumber, secondNumber) => Math.floor(Number(firstNumber) / Number(secondNumber));
const multiply = (firstNumber, secondNumber) => Number(firstNumber) * Number(secondNumber);

export { add, minus, divide, multiply };
